	RealType function hddSM_sumup(CCloop, CCtree)
	implicit none

#include "hddSM-vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	hddSM_sumup = 0

	m = 0
	m = m + CCloop(1)*MatSUN(1,1)
	hddSM_sumup = hddSM_sumup + Re(Conjugate(CCtree(1))*m)
	end

