	subroutine hddSM_SquaredMEHel(result)
	implicit none
	RealType result(*)

#include "hddSM-vars.h"

	RealType hddSM_sumup
	external hddSM_sumup

* hddSM-BEGIN ABBR_HEL
	call hddSM_abbr0hel
	call hddSM_abbr1hel
* hddSM-END ABBR_HEL

* hddSM-BEGIN FF_INI
	Ctree(1) = 0

	Cloop(1) = 0
* hddSM-END FF_INI

* hddSM-BEGIN FF_TREE
	call hddSM_v0

	result(1) = result(1) + hddSM_sumup(Ctree, Ctree)
* hddSM-END FF_TREE

* hddSM-BEGIN FF_LOOP
	call hddSM_v1

	LOOP(Gen4, 1,3,1)
	call hddSM_v1_Gen4
	ENDLOOP(Gen4)

	result(2) = result(2) + 2*hddSM_sumup(Cloop, Ctree)
	result(2) = result(2) + hddSM_sumup(Cloop, Cloop)
* hddSM-END FF_LOOP
	end

