	RealType function hgagaSM_sumup(CCloop, CCtree)
	implicit none

#include "hgagaSM-vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	hgagaSM_sumup = 0

	m = 0
	m = m + CCloop(1)*1
	hgagaSM_sumup = hgagaSM_sumup + Re(Conjugate(CCtree(1))*m)
	end

