	subroutine hgagaSM_SquaredMEHel(result)
	implicit none
	RealType result(*)

#include "hgagaSM-vars.h"

	RealType hgagaSM_sumup
	external hgagaSM_sumup

* hgagaSM-BEGIN ABBR_HEL
	call hgagaSM_abbr1hel
* hgagaSM-END ABBR_HEL

* hgagaSM-BEGIN FF_INI
	Cloop(1) = 0
* hgagaSM-END FF_INI

* hgagaSM-BEGIN FF_LOOP
	call hgagaSM_v1

	LOOP(Gen4, 1,3,1)
	call hgagaSM_v1_Gen4
	ENDLOOP(Gen4)

	result(2) = result(2) + hgagaSM_sumup(Cloop, Cloop)
* hgagaSM-END FF_LOOP
	end

