	RealType function huu_sumup(CCloop, CCtree)
	implicit none

#include "huu-vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	huu_sumup = 0

	m = 0
	m = m + CCloop(1)*MatSUN(1,1)
	huu_sumup = huu_sumup + Re(Conjugate(CCtree(1))*m)
	end

