	RealType function huuSM_sumup(CCloop, CCtree)
	implicit none

#include "huuSM-vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	huuSM_sumup = 0

	m = 0
	m = m + CCloop(1)*MatSUN(1,1)
	huuSM_sumup = huuSM_sumup + Re(Conjugate(CCtree(1))*m)
	end

