	subroutine huuSM_SquaredMEHel(result)
	implicit none
	RealType result(*)

#include "huuSM-vars.h"

	RealType huuSM_sumup
	external huuSM_sumup

* huuSM-BEGIN ABBR_HEL
	call huuSM_abbr0hel
	call huuSM_abbr1hel
* huuSM-END ABBR_HEL

* huuSM-BEGIN FF_INI
	Ctree(1) = 0

	Cloop(1) = 0
* huuSM-END FF_INI

* huuSM-BEGIN FF_TREE
	call huuSM_v0

	result(1) = result(1) + huuSM_sumup(Ctree, Ctree)
* huuSM-END FF_TREE

* huuSM-BEGIN FF_LOOP
	call huuSM_v1

	LOOP(Gen4, 1,3,1)
	call huuSM_v1_Gen4
	ENDLOOP(Gen4)

	result(2) = result(2) + 2*huuSM_sumup(Cloop, Ctree)
	result(2) = result(2) + huuSM_sumup(Cloop, Cloop)
* huuSM-END FF_LOOP
	end

