************************************************************************

	double precision function sumup(CCloop, CCtree)
	implicit none

#include "vars.h"

	double complex CCloop(1), CCtree(1)
	double complex m

	sumup = 0

	m = 0
	m = m + CCloop(1)*1
	sumup = sumup + DBLE(DCONJG(CCtree(1))*m)
	end

